namespace LeetCode_1768._Merge_Strings_Alternately;public class Solution{    public string MergeAlternately(string word1, string word2)    {        var maxLength = word1.Length + word2.Length;        var charArray = new char[maxLength];        var wordPos = 0;        for (var i = 0; i < maxLength; i++)        {            if (i < word1.Length) charArray[wordPos++] = word1[i];            if (i < word2.Length) charArray[wordPos++] = word2[i];        }        return new string(charArray);    }}