namespace LeetCode_20._Valid_Parentheses;public class Solution{    public bool IsValid(string s)    {        var map = new Dictionary<char, int>();        foreach (var c in s)            switch (c)            {                case '(':                case ')':                {                    var key = '(';                    var value = GetOrDefault(map, key, 0);                    map[key] = ++value;                    break;                }                case '{':                case '}':                {                    var key = '{';                    var value = GetOrDefault(map, key, 0);                    map[key] = ++value;                    break;                }                case '[':                case ']':                {                    var key = '[';                    var value = GetOrDefault(map, key, 0);                    map[key] = ++value;                    break;                }            }        foreach (var (_, value) in map)            if (value % 2 == 1)                return false;        return true;    }    private static int GetOrDefault(IReadOnlyDictionary<char, int> dictionary, char key, int defaultValue)    {        var value = defaultValue;        try        {            value = dictionary[key];        }catch { }        return value;    }}