namespace LeetCode_20._Valid_Parentheses;public class Solution{    public bool IsValid(string s)    {        var thisRun = false;        do        {            for (var i = 0; i < s.Length; i++)            {                if (i == 0) thisRun = false;                if (i + 1 < s.Length)                    if (s[i + 1] == GetClosing(s[i]))                    {                        thisRun = true;                        s = s.Remove(i, 2);                        continue;                    }                if (i == s.Length - 1 && !thisRun)                    return false;            }        } while (s.Length > 0);        return true;    }    private static char? GetClosing(char open)    {        return open switch        {            '(' => ')',            '{' => '}',            '[' => ']',            _ => null        };    }}